mapper.select:
SELECT count(DISTINCT sno) FROM device
WHERE owner_id= #{sysUserId}
AND is_deleted = 0
and status != 6

    AND product_id = #{productId}

and ctime < #{endTime}

SELECT COUNT(DISTINCT sno) FROM device
WHERE ctime between #{start} and #{end}
AND is_deleted = 0
AND owner_id = #{sysUserId}
AND product_id = #{productId}
AND status !=6

SELECT COUNT(DISTINCT sno) FROM device
where is_deleted = 0
AND owner_id = #{sysUserId}
AND product_id = #{productId}
AND status !=6
AND (online_status = 1 or last_online_time between #{start} and #{end})

SELECT COUNT(DISTINCT d.`sno`) FROM device d,order_base o
WHERE o.`sno` = d.`sno`
AND o.`ctime` between #{start} and #{end}
AND d.`owner_id` = #{sysUserId}
AND d.is_deleted=0
and d.status !=6

    AND d.`product_id` = #{productId}

SELECT COUNT(DISTINCT d.`sno`) FROM device d,device_alarm a WHERE d.`sno` = a.`sno` AND a.`alarm_type` = 1 and d.is_deleted=0 and d.fault_status = 6 and a.status = 0
AND product_id = #{productId}
AND d.`owner_id` = #{sysUserId}

SELECT COUNT(DISTINCT d.`sno`) FROM device d,device_alarm a WHERE d.`sno` = a.`sno` AND a.`alarm_type` = 2 and d.is_deleted=0 and d.fault_status = 6 and a.status = 0
AND d.`product_id` = #{productId}
AND d.`owner_id` = #{sysUserId}

SELECT o.`sno` FROM `user` u,order_base o
WHERE o.`user_id` = u.`id`
AND o.`order_status` = #{status}
AND u.`openid` = #{openid}
ORDER BY o.`ctime` DESC LIMIT 0,1

select COUNT(DISTINCT sno) from device where owner_id = #{ownerId}
and  launch_area_id is  null and is_deleted = 0 and status !=6

    AND `product_id` = #{productId}

SELECT COUNT(sno) FROM device
WHERE is_deleted = 0 AND status != 6
AND owner_id = #{sysUserId}
AND product_id = #{productId}
AND online_status = 1

SELECT COUNT(sno) FROM device
WHERE is_deleted = 0 AND status != 6
AND owner_id = #{sysUserId}
AND product_id = #{productId}
AND activate_status = 2

    AND activated_time  >=  #{startTime}


    AND activated_time  <  #{endTime}

SELECT COUNT(DISTINCT d.sno) FROM device d JOIN device_alarm a ON d.sno = a.sno
WHERE d.is_deleted = 0 AND d.status != 6
AND d.owner_id = #{sysUserId}
AND d.product_id = #{productId}
AND a.alarm_type = 1
AND a.ctime  <  #{endTime}
AND (
  a.status = 0
  OR
  (a.status = 1 AND a.fixed_time IS NOT NULL AND a.fixed_time  >=  #{endTime})
)

SELECT COUNT(DISTINCT d.sno) FROM device d JOIN device_alarm a ON d.sno = a.sno
WHERE d.is_deleted = 0 AND d.status != 6
AND d.owner_id = #{sysUserId}
AND d.product_id = #{productId}
AND a.alarm_type = 2
AND a.ctime  <  #{endTime}
AND (
  a.status = 0
  OR
  (a.status = 1 AND a.fixed_time IS NOT NULL AND a.fixed_time  >=  #{endTime})
)

SELECT province,COUNT(DISTINCT d.`sno`) AS count FROM device d
WHERE  d.`owner_id` = #{sysUserId}
and d.is_deleted =0
and d.status !=6

    AND d.product_id = #{productId}

GROUP BY province

SELECT * FROM ( SELECT count( d1.sno ) AS totalCount FROM device d1 WHERE d1.is_deleted = 0
and d1.product_id=#{productId}
) total JOIN ( SELECT count( d2.sno ) AS yesterNewCount FROM device d2 WHERE d2.is_deleted = 0
and d2.product_id=#{productId}
and date_format(d2.ctime,'%Y-%m-%d') = date_format(#{date},'%Y-%m-%d')
) AS yesterAdd JOIN ( SELECT count( d3.sno ) AS onlineCount FROM device d3 WHERE d3.is_deleted = 0
and d3.product_id=#{productId}
AND d3.online_status = 1 ) AS onlineDevice JOIN ( SELECT count( d4.sno ) AS activeCount FROM device d4 WHERE d4.is_deleted = 0
and d4.product_id=#{productId}
AND d4.work_status = 3 ) AS activeDevice

SELECT * FROM ( SELECT count( d1.sno ) AS totalCount FROM device d1 WHERE d1.is_deleted = 0
and d4.product_id=#{productId}
) AS total JOIN ( SELECT count( d2.sno ) AS faultCount FROM device d2 WHERE d2.is_deleted = 0 AND d2.work_status = 6
and d4.product_id=#{productId}
) AS fault JOIN ( SELECT count( d3.sno ) AS warnCount FROM device d3 WHERE d3.is_deleted = 0 AND d3.work_status = 8
and d4.product_id=#{productId}
) AS warn

select d.province as province, count(d.province) as deviceCount from device d where d.is_deleted=0

    and d.product_id=#{productId}

 GROUP BY d.province

select d.city as province, count(d.city) as deviceCount from device d where d.is_deleted=0

    and d.province=#{province}


    and d.product_id=#{productId}

GROUP BY d.city
